INSERT INTO `pages` (`id`, `name`, `content`, `alt_name`) VALUES
(1, 'О проекте', '<p>\n	&nbsp;</p>\n<p style=\"color: rgb(0, 0, 0); font-family: \'Times New Roman\', Times, serif; font-size: 16px; margin-bottom: 0.14in;\">\n	<font face=\"Arial, serif\"><font size=\"3\">О проекте&nbsp;</font></font><font face=\"Arial, serif\"><font size=\"3\"><span lang=\"en-US\" xml:lang=\"en-US\">MebelBoom</span></font></font><font face=\"Arial, serif\"><font size=\"3\">.</font></font><font face=\"Arial, serif\"><font size=\"3\"><span lang=\"en-US\" xml:lang=\"en-US\">com</span></font></font></p>\n<p style=\"color: rgb(0, 0, 0); font-family: \'Times New Roman\', Times, serif; font-size: 16px; margin-bottom: 0.14in;\">\n	<font face=\"Arial, serif\"><font size=\"3\"><span lang=\"en-US\" xml:lang=\"en-US\">MebelBoom</span></font></font><font face=\"Arial, serif\"><font size=\"3\">.</font></font><font face=\"Arial, serif\"><font size=\"3\"><span lang=\"en-US\" xml:lang=\"en-US\">com</span></font></font><font face=\"Arial, serif\"><font size=\"3\">&nbsp;&ndash; интернет проект, посвященный мебельной отрасли города Казани и Москвы. </font></font></p>\n<p style=\"color: rgb(0, 0, 0); font-family: \'Times New Roman\', Times, serif; font-size: 16px; margin-bottom: 0.14in;\">\n	<font face=\"Arial, serif\"><font size=\"3\">Главная цель проекта &ndash; быстрый и удобный поиск мебели различного назначения. </font></font></p>\n<p style=\"color: rgb(0, 0, 0); font-family: \'Times New Roman\', Times, serif; font-size: 16px; margin-bottom: 0.14in;\">\n	<font face=\"Arial, serif\"><font size=\"3\">На сайте собрана информация о самых разных видах мебели. Для удобного поиска информация структурируется по категориям и разделам.</font></font><br />\n	<br />\n	&nbsp;</p>\n<p style=\"color: rgb(0, 0, 0); font-family: \'Times New Roman\', Times, serif; font-size: 16px; margin-bottom: 0.14in;\">\n	<font face=\"Arial, serif\"><font size=\"3\">Потенциальный потребитель, заходящий на наш портал с целью покупки мебели, решает следующие задачи:</font></font></p>\n<ul style=\"color: rgb(0, 0, 0); font-family: \'Times New Roman\', Times, serif; font-size: 16px;\">\n	<li>\n		<p style=\"margin-bottom: 0.14in;\">\n			<font face=\"Arial, serif\"><font size=\"3\">Поиск магазина / производства / склада, а также их контактные данные</font></font></p>\n	</li>\n	<li>\n		<p style=\"margin-bottom: 0.14in;\">\n			<font face=\"Arial, serif\"><font size=\"3\">Поиск готового товара (мебель, фурнитура)</font></font></p>\n	</li>\n	<li>\n		<p style=\"margin-bottom: 0.14in;\">\n			<font face=\"Arial, serif\"><font size=\"3\">Поиск производителя мебели на заказ</font></font></p>\n	</li>\n	<li>\n		<p style=\"margin-bottom: 0.14in;\">\n			<font face=\"Arial, serif\"><font size=\"3\">Оформление заказа на готовый товар, либо на производство мебели</font></font></p>\n	</li>\n	<li>\n		<p style=\"margin-bottom: 0.14in;\">\n			<font face=\"Arial, serif\"><font size=\"3\">Подписка на рассылку портала (проходящие акции в городе Казань)</font></font></p>\n	</li>\n	<li>\n		<p style=\"margin-bottom: 0.14in;\">\n			<font face=\"Arial, serif\"><font size=\"3\">Просмотр и выставление рейтинга мебельным магазинам и их товарам, а также возможность оставить отзыв</font></font></p>\n	</li>\n</ul>\n<p style=\"color: rgb(0, 0, 0); font-family: \'Times New Roman\', Times, serif; font-size: 16px; margin-bottom: 0.14in;\">\n	<br />\n	<font face=\"Arial, serif\"><font size=\"3\"><span lang=\"en-US\" xml:lang=\"en-US\"><b>MebelBoom</b></span></font></font><font face=\"Arial, serif\"><font size=\"3\"><b>.</b></font></font><font face=\"Arial, serif\"><font size=\"3\"><span lang=\"en-US\" xml:lang=\"en-US\"><b>com</b></span></font></font><font face=\"Arial, serif\"><font size=\"3\"><b>&nbsp;&ndash; это:</b></font></font><br />\n	<font face=\"Arial, serif\"><font size=\"3\">УДОБНО &ndash; вся информация о мебели в одном портале;<br />\n	ОПЕРАТИВНО &ndash; самая свежая информация;<br />\n	ПРОФЕССИОНАЛЬНО &ndash; сайт специализируется только на мебельной тематики;<br />\n	ВЫГОДНО &ndash; экономия времени и денег;<br />\n	ЭФФЕКТИВНО - покупатель и продавец стали ближе друг к другу.<br />\n	Добро пожаловать на&nbsp;</font></font><font face=\"Arial, serif\"><font size=\"3\"><span lang=\"en-US\" xml:lang=\"en-US\">MebelBoom</span></font></font><font face=\"Arial, serif\"><font size=\"3\">.</font></font><font face=\"Arial, serif\"><font size=\"3\"><span lang=\"en-US\" xml:lang=\"en-US\">com</span></font></font></p>\n<div>\n	&nbsp;</div>\n', 'about'),
(2, 'Для продавцов', '<p>\n	Раздел в разработке</p>\n', 'for_resellers'),
(3, 'Реклама', '<p style=\"text-align:left;line-height:19px;\">\n	<span style=\"font-family:Arial;font-size:12px;font-weight:normal;font-style:normal;text-decoration:none;color:#333333;\">Ваша компания работает в мебельной отрасли? Размещение рекламы на mebelboom </span><span style=\"font-family:Arial;font-size:12px;font-weight:normal;font-style:normal;text-decoration:none;color:#333333;\">&mdash;</span><span style=\"font-family:Arial;font-size:12px;font-weight:normal;font-style:normal;text-decoration:none;color:#333333;\"> это прямое обращение к вашей целевой аудитории.</span></p>\n', 'reclame'),
(5, 'Ваши магазины и товары на mebelboom.com', '<p>\n	<span style=\"font-family:Arial;font-size:12px;font-weight:normal;font-style:normal;text-decoration:none;color:#333333;\">Размещение информации о ваших товарных предложениях на mebelboom.com - это надежный способ привлечь внимание потенциальных клиентов.</span></p>\n<p style=\"text-align:left;line-height:19px;\">\n	<span style=\"font-family:Arial;font-size:12px;font-weight:bold;font-style:normal;text-decoration:none;color:#333333;\">Интернет-магазины</span><span style=\"font-family:Arial;font-size:12px;font-weight:normal;font-style:normal;text-decoration:none;color:#333333;\"> могут разместить свои предложения в каталоге &quot;Мебель&quot;</span></p>\n<table border=\"0\" cellpadding=\"2\" cellspacing=\"0\" style=\"width: 550px;\">\n	<tbody>\n		<tr>\n			<td>\n				<p>\n					<img alt=\"\" height=\"155\" src=\"/images/feniks.jpg\" width=\"201\" /></p>\n				<p>\n					<span style=\"font-family:Arial;font-size:12px;font-weight:normal;font-style:italic;text-decoration:none;color:#333333;\">Предложения в каталоге</span></p>\n			</td>\n			<td>\n				<p>\n					<img alt=\"\" height=\"155\" src=\"/images/gloria.jpg\" width=\"239\" /></p>\n				<p>\n					<span style=\"font-family:Arial;font-size:12px;font-weight:normal;font-style:italic;text-decoration:none;color:#333333;\">Информация об интернет-магазине</span></p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n<p>\n	&nbsp;</p>\n<p>\n	Оффлайн-магазины могут разместить информацию о своих магазинах в каталоге &quot;Магазины&quot;, и свои товары в каталоге &quot;Мебель&quot;.</p>\n<table align=\"left\" border=\"0\" cellpadding=\"2\" cellspacing=\"0\" style=\"width: 550px;\">\n	<tbody>\n		<tr>\n			<td>\n				<p>\n					<img alt=\"\" src=\"/images/gloria.jpg\" style=\"width: 232px; height: 155px;\" /></p>\n				<p>\n					<span style=\"font-family:Arial;font-size:12px;font-weight:normal;font-style:italic;text-decoration:none;color:#333333;\">Каталог магазинов</span></p>\n			</td>\n			<td>\n		