INSERT INTO `slides` (`id_song`, `id_slide`, `text`, `active`, `id`) VALUES
(172, 0, 'Одного лишь <br/>\nпросил я у Господа<br/>\nЛишь одного я ищу<br/>\nЧтобы мне быть <br/>\nв доме Твоём<br/>\nВзирать на Твою красоту ', 1, 1876),
(172, 1, 'Ведь могут отнять <br/>\nи богатство, и славу,<br/>\nМогут отнять <br/>\nпищу и кров<br/>\nТолько никто <br/>\nникогда не отнимет<br/>\nЛишь одного ', 1, 1877),
(172, 2, 'Это Божья любовь!<br/>\nЭто Божья любовь! ', 1, 1878),
(172, 3, 'Сердце мое <br/>\nговорит от Тебя<br/>\nИщите лица Моего<br/>\nИ я пойду <br/>\nи буду искать<br/>\nИ наконец-то найду ', 1, 1879),
(172, 4, 'Ведь могут отнять <br/>\nи богатство, и славу,<br/>\nМогут отнять <br/>\nпищу и кров<br/>\nТолько никто <br/>\nникогда не отнимет<br/>\nЛишь одного ', 1, 1880),
(172, 5, 'Это Божья любовь!<br/>\nЭто Божья любовь! ', 1, 1881),
(227, 0, 'Слава Слава  <br/>\nБогу Всевышнему<br/>\nСлава Слава  <br/>\nБогу Всесильному ', 1, 2090),
(227, 1, 'Золото небес <br/>\nжемчужина земли<br/>\nКрепкая скала – Иисус<br/>\nВечная любовь <br/>\nсмысл бытия            <br/>\nСвятая драгоценность  Иисус ', 1, 2091),
(227, 2, 'Да склонятся горы <br/>\nпред лицом Твоим<br/>\nБудут петь все звёзды <br/>\nна небесах<br/>\nПоклонятся народы племена земли<br/>\nБудут петь все ангелы <br/>\nв небесах. ', 1, 2092),
(275, 0, 'Христом едины мы стоим<br/>\nОн даровал нам <br/>\nсвет любви<br/>\nТворец вселенной <br/>\nВечный Бог ', 1, 2273),
(275, 1, 'Сквозь бури <br/>\nштормы нас ведёт<br/>\nО бездна мира и любви<br/>\nЕй силы тьмы поглощены<br/>\nМой Искупитель <br/>\nвсё во всём ', 1, 2274),
(275, 2, 'Сошел на землю <br/>\nпринял плоть<br/>\nВ Младенце к нам <br/>\nпришёл Господь<br/>\nВеликий дар святой любви<br/>\nСтрадал Спаситель <br/>\nвсей земли ', 1, 2275),
(275, 3, 'Ведь на кресте Он умирал<br/>\nЧтоб Бог тебя не наказал<br/>\nВесь грех Христос <br/>\nраспял Собой<br/>\nЧтоб живы были <br/>\nмы с тобой ', 1, 2276),
(275, 4, 'В пещере тёмной погребён<br/>\nСвет злобой мрака <br/>\nбыл стеснён<br/>\nНо в славный день <br/>\nХристос воскрес,<br/>\nРазрушил смерть <br/>\nЦарь Небес ', 1, 2277),
(275, 5, 'Восстал в победе <br/>\nнавсегда<br/>\nИ я свободен от греха<br/>\nВедь я Его <br/>\nИисус мой Царь<br/>\nИскуплен кровью Агнца ', 1, 2278),
(275, 6, 'Нет обвиненья, страха нет<br/>\nБог заключил <br/>\nсо мной завет<br/>\nС начала жизни до конца<br/>\nСудьба моя в руках Отца ', 1, 2279),
(275, 7, 'Ни силы тьмы ни человек<br/>\nНе разлучат <br/>\nнас с Ним вовек<br/>\nГосподь грядёт <br/>\nмы ждём Его<br/>\nХранит нас Дух Святой Его ', 1, 2280),
(335, 0, 'Прими хвалу наших сердец<br/>\nСвятой, Святой<br/>\nТы Тот Кто умер и воскрес<br/>\nСвятой, Святой ', 1, 2536),
(335, 1, 'Взываем мы к Тебе, Отец<br/>\nСвятой, Святой<br/>\nИзлей Свой дождь на нас с небес<br/>\nСвятой, Святой ', 1, 2537),
(335, 2, 'СВЯТОЙ, СВЯТОЙ, <br/>\nСВЯТОЙ<br/>\nБОГ ', 1, 2538),
(58, 0, 'Господь – сила моя<br/>\nБог мой – крепость моя<br/>\nМоя праведность<br/>\nМоё спасение <br/>\nИ веры щит ', 1, 2893),
(58, 1, 'И потому я пою <br/>\nИ радуюсь<br/>\nПесни пою и радуюсь <br/>\nСлавлю Бога<br/>\nСильного моего ', 1, 2894),
(258, 0, 'Ты - моя Лоза<br/>\nЯ - веточка Твоя<br/>\nЯ -  веточка Твоя<br/>\nТы - моя Лоза<br/>\nЯ -  веточка Твоя<br/>\nПусть мир<br/>\n во мне увидит<br/>\nСына Божьего ', 1, 4352),
(258, 1, 'Ты - моя Лоза<br/>\nЯ - веточка Твоя<br/>\nЯ -  веточка Твоя<br/>\nТы - моя Лоза<br/>\nТы - моя Лоза<br/>\nЯ - веточка Твоя<br/>\nДай мне познать Тебя<br/>\nСына Божьего ', 1, 4353),
(258, 2, 'Дай мне <br/>\nпознать Тебя<br/>\nСына Божьего ', 1, 4354),
(359, 0, 'А Ты как птица у меня в руке<br/>\nТо взлетишь, то прилетишь опять.<br/>\nПрикоснёшься к моей судьбе<br/>\nИ опять научишь летать. ', 1, 4583),
(359, 1, 'А ты как пуля в сердце моём,<br/>\nЧто застряла так глубоко.<br/>\nПорой хочу Тебя я достать,<br/>\nА как достану, мне опять не легко. ', 1, 4584),
(359, 2, 'Ты прилети ко мне опять<br/>\nИ застрянь глубже ещё.<br/>\nДа, чтоб Тебя никогда не достать<br/>\nИ в своём сердце закрыть ключом. ', 1, 4585),
(359, 3, 'А Ты как ветер, что ворвался в меня<br/>\nВсё разломал и построил опять<br/>\nИ в моём сердце разбросал семена<br/>\nИ позволил Себя растоптать ', 1, 4586),
(359, 4, 'А Ты как птица...<br/>\n А Ты как ветер...<br/>\nА Ты как пуля, что попала в меня. ', 1, 4587),
(359, 5, 'Когда темно – <br/>\nкак солнце светишь.<br/>\nКогда мне больно - ласкаешь меня. ', 1, 4588),
(359, 6, 'А Ты как птица... <br/>\nА Ты как ветер...<br/>\nА Ты как пуля, что попала в меня. ', 1, 4589),
(359, 7, 'А ты как мина одна на поле<br/>\nИ я опять наступил на Тебя. ', 1, 4590),
(2, 0, 'Петь у Твоих ног <br/>\nя бы вечность мог<br/>\nСклоняюсь я перед Тобой<br/>\nОдетый в благодать <br/>\nя не могу стоять<br/>\nВ присутствии Твоём <br/>\nВеликий Бог. ', 1, 4594),
(2, 1, 'Люблю Тебя, <br/>\nГосподь, Авва Отче<br/>\nИщу Тебя, <br/>\nГосподь мой<br/>\nВсем сердцем петь Тебе <br/>\nднём и ночью<br/>\nЛюблю Тебя, Господь мой ', 1, 4595),
(2, 2, 'Господь я восхищён <br/>\nчто Ты меня нашёл<br/>\nЯ звал Тебя <br/>\nи Ты пришёл<br/>\nЗаветная мечта <br/>\nво мне горит опять<br/>\nТебя как друга <br/>\nя желаю знать ', 1, 4596),
(2, 3, 'Господь Ты мой спаситель <br/>\nТы слава для меня<br/>\nТебе всё отдаю я <br/>\nТы жизнь моя, Ты жизнь моя<br/>\nПокрытый благодатью <br/>\nя здесь через Твой крест<br/>\nГосподь <br/>\nТы любовь моя навек ', 1, 4597),
(299, 0, 'Бог излил любовь на всех нас    <br/>\nОн даровал прощение <br/>\nВ Его имени спасенье <br/>\nРадость, исцеление ', 1, 4665),
(299, 1, 'Бог Создатель, мира Князь    <br/>\nТы первый и последний  <br/>\nЯ буду петь, буду кричать   <br/>\nО милости Твоей ', 1, 4666),
(299, 2, 'Вечно буду славить лишь Тебя       <br/>\nТы открыл нам двери в небеса   <br/>\nВоспою Тебя ведь Ты святой        <br/>\nАллилуйя! ', 1, 4667),
(299, 3, 'Аллилуйя, Аллилуйя <br/>\nАллилуйя, Ты Святой ', 1, 4668),
(28, 0, 'В мою жизнь <br/>\nПришёл Твой свет<br/>\nРазогнав печали<br/>\nТы принёс мне жизнь Свою<br/>\nЗа любовь Твою<br/>\nТебя я прославляю<br/>\nИ теперь я для Тебя<br/>\nМой Бог живу. ', 1, 4759),
(28, 1, 'Твоя любовь изменила <br/>\nжизнь мою<br/>\nТвоя любовь подняла <br/>\nменя на высоту<br/>\nТвоя любовь <br/>\nзащитила, сохранила<br/>\nТвоя любовь ', 1, 4760),
(28, 2, 'Ты открыл мои глаза<br/>\nЯ увидел грех мой<br/>\nТы его взял на Себя<br/>\nИ теперь с Тобой, Иисус,<br/>\nМы будем вместе<br/>\nНе оставлю я Тебя<br/>\nНикогда<br/>\nТеперь живу я для Тебя ', 1, 4761),
(40, 0, 'К награде я стремлюсь,<br/>\nВсе силы отдаю<br/>\nИ всё что есть во мне<br/>\nЖивёт для Бога.<br/>\nНичто и никогда<br/>\nНе отделит меня<br/>\nОт плана Твоего ', 1, 4813),
(40, 1, 'И знаю я, что<br/>\nТот Кто во мне<br/>\nНамного сильней того<br/>\nкто в миру живёт.<br/>\nВерой сдвину<br/>\nдаже горы<br/>\nИ во Христе я всё смогу ', 1, 4814),
(40, 2, 'Я знаю, <br/>\nверой стоять <br/>\nя буду твёрдо<br/>\nИ укрепит меня Господь!<br/>\nВсё смогу! ', 1, 4815),
(63, 0, 'Давай хвалу ему кричать<br/>\nОн дал сил нам волю Божью исполнять<br/>\nОн пришел с небес к нам чтобы спасти нас ', 1, 4932),
