(99, 4, 'Ни в чём не нуждаюсь,<br/>\nКогда у меня есть <br/>\nТы! ', 1, 9435),
(109, 0, 'Иисус, превозносим <br/>\nТвое имя, Ты Царь,<br/>\nПребывающий <br/>\nсреди нас<br/>\nИ наполняющий сердца ', 1, 9436),
(109, 1, 'Мы поклоняемся Тебе, Господь,<br/>\nТы Бог на небе и на земле<br/>\nТы Бог на небе и на земле<br/>\nТы Князь мира во веки веков ', 1, 9437),
(109, 2, 'Иисус, о Господь Тебя <br/>\nИменуем мы Царём<br/>\nСущего между нами<br/>\nМы поднимаем вверх хвалой ', 1, 9438),
(109, 3, 'Хвалой мы строим Тебе престол<br/>\nО, приди, воссядь <br/>\nна нём ', 1, 9439),
(109, 4, 'Мы поклоняемся Тебе, Господь,<br/>\nТы Бог на небе и на земле<br/>\nТы Бог на небе и на земле<br/>\nТы Князь мира во веки веков ', 1, 9440),
(105, 0, 'Иисус <br/>\n сейчас здесь! ', 1, 9441),
(105, 1, 'Его могучая <br/>\nсила здесь <br/>\nчтоб спасти тебя ', 1, 9442),
(105, 2, 'Его могучая сила здесь <br/>\nисцелит тебя ', 1, 9443),
(105, 3, 'Его могучая сила здесь <br/>\nосвободит тебя ', 1, 9444),
(101, 0, 'Иисус - сила Бога<br/>\nИисус – мудрость Бога<br/>\nИисус – полнота Божества ', 1, 9445),
(101, 1, 'Исполненье <br/>\nдоброй воли Отца<br/>\nПримиренье неба и земли<br/>\nИстребитель <br/>\nсмерти и греха<br/>\nВечная опора<br/>\nВоскресший Сын ', 1, 9446),
(101, 2, 'Слава Тебе <br/>\nслава, слава Тебе <br/>\nслава, слава Тебе <br/>\nСлава! ', 1, 9447),
(100, 0, 'Иисус, Ты Лоза <br/>\nа мы ветви<br/>\nСоединённые в одно<br/>\nИисус, Ты Лоза <br/>\nа мы ветви<br/>\nВ винограднике Твоём ', 1, 9448),
(100, 1, 'Иисус, Ты Лоза <br/>\nа мы ветви<br/>\nСоком Твоим я живу<br/>\nИисус, Ты Лоза <br/>\nа мы ветви<br/>\nНа корне святом <br/>\nя стою ', 1, 9449),
(100, 2, 'Поднимаюсь, обновляюсь<br/>\nДухом Твоим, Иисус<br/>\nРасцветаю, оживаю,<br/>\nБез Тебя ничего <br/>\nне могу ', 1, 9450),
(100, 3, 'Расцветаю и взлетаю,<br/>\nБез Тебя ничего <br/>\nне могу ', 1, 9451),
(106, 0, 'Иисус <br/>\nя славлю Тебя<br/>\nЗа всё <br/>\nчто сделал Ты<br/>\nБлагодарю я Тебя<br/>\nЗа крест, за Кровь Твою ', 1, 9452);
INSERT INTO `slides` (`id_song`, `id_slide`, `text`, `active`, `id`) VALUES
(106, 1, 'Хочу идти за Тобой<br/>\nКуда б Ты <br/>\nни послал меня<br/>\nЯ славлю Тебя<br/>\nЯ лишь Тебя люблю ', 1, 9453),
(106, 2, 'Пусть Твой огонь<br/>\n    cожжёт всё, что<br/>\nТебе мешает<br/>\nПусть Дух Святой<br/>\nДождём прольётся <br/>\nв жизнь мою ', 1, 9454),
(106, 3, 'Я славлю Тебя<br/>\nВсю жизнь Тебе <br/>\nя посвящаю<br/>\nТы - мой Господь Иисус ', 1, 9455),
(106, 4, 'Ты - мой Господь, Иисус! ', 1, 9456),
(111, 0, 'Иисус, <br/>\nя так люблю Тебя Иисус,<br/>\nТы воссиял <br/>\nСвоим светом<br/>\nВ сердце моём ', 1, 9457),
(111, 1, 'Знаю <br/>\nТвой Дух во мне пребывает<br/>\nМеня Он преображает<br/>\nНочью и днём ', 1, 9458),
(111, 2, 'Милостью Твоей взлетаю<br/>\nМилостью живу <br/>\nи знаю любишь <br/>\nзнаю любишь <br/>\nТы меня ', 1, 9459),
(111, 3, 'Твой огонь <br/>\nлюбви Небесной<br/>\nЛьётся в моём сердце песней,<br/>\nЛьётся этой песней для Тебя ', 1, 9460),
(111, 4, 'Нежность <br/>\nТы мне открыл <br/>\nСвою нежность<br/>\nСвою Отцовскую верность<br/>\nМир Свой и покой ', 1, 9461),
(111, 5, 'Отче <br/>\nТы мне открыл <br/>\nмои очи<br/>\nИ стали светлыми ночи<br/>\nИ радость льётся рекой ', 1, 9462),
(111, 6, 'Милостью Твоей взлетаю,<br/>\nМилостью живу <br/>\nи знаю любишь, <br/>\nзнаю любишь Ты меня ', 1, 9463),
(111, 7, 'Твой огонь <br/>\nлюбви Небесной<br/>\nЛьётся в моём сердце песней,<br/>\nЛьётся этой песней для Тебя ', 1, 9464),
(1103, 0, 'И Ходаем, Үзең өйрәт мине<br/>\nЧын күңлемнән дога кылырга.<br/>\nБуйсындырып горур йөрәгемне,<br/>\nСинең алда ихлас булырга. ', 1, 9465),
(1103, 1, 'өйрәт мине Синең белән генә<br/>\nБәхетле hәм көчле булырга.<br/>\nШатлык hәм кайгылар килгәндә дә<br/>\nТик синең артыннан барырга. ', 1, 9466),
(1103, 2, 'өйрәт мине тирән сөю белән<br/>\nЯратырга якыннарымны.<br/>\nЧистартырга мине гөнаhлардан<br/>\nКызганмадын изге каныңны. ', 1, 9467),
(457, 0, 'Ищите прежде Царства Божия<br/>\nИ правды Его,<br/>\nВсё остальное приложится вам,<br/>\nАллилуйя, Аллилуйя! ', 1, 9468),
(457, 1, 'Царство Божие не пища и питьё,<br/>\nА праведность и мир,<br/>\nИ радость во Святом Духе,<br/>\nАллилуйя, Аллилуйя! ', 1, 9469),
(457, 2, 'Просите и дано будет<br/>\nвам,<br/>\nИщите и найдёте,<br/>\nСтучите и отворят вам,<br/>\nАллилуйя, Аллилуйя! ', 1, 9470),
(457, 3, 'Время приблизилось царства Его,<br/>\nВерьте и ждите Христа.<br/>\nОн призывает всех хочет спасти,<br/>\nАллилуйя, Аллилуйя! ', 1, 9471),
(115, 0, 'Каждый день на устах моих<br/>\nЭто сладкое имя  - Иисус<br/>\nЯ не знаю имён других<br/>\nЕго имя превозношу ', 1, 9472),
(115, 1, 'Моё сердце поёт о Нём,<br/>\nО Спасителе <br/>\nдивном моём.<br/>\nТолько Он мне <br/>\nдаёт покой,<br/>\nЕго имя всегда со мной<br/>\nВсегда со мной ', 1, 9473),
(115, 2, 'Я буду славить <br/>\nГоспода Христа!<br/>\nЯ буду помнить <br/>\nвсе Его дела!<br/>\nЯ буду петь Ему Аллилуйя! Аллилуйя! ', 1, 9474),
(115, 3, 'И когда солнца <br/>\nсвет горит,<br/>\nСогревая всю землю теплом,<br/>\nВ моём сердце опять звучит<br/>\nИисусу–Царю псалом! ', 1, 9475),
(115, 4, 'Я буду славить <br/>\nГоспода Христа!<br/>\nЯ буду помнить <br/>\nвсе Его дела!<br/>\nЯ буду петь Ему Аллилуйя! Аллилуйя! ', 1, 9476),
(115, 5, 'Аллилуйя! ', 1, 9477),
(116, 0, 'Каждый день <br/>\nоткрывает двери <br/>\nЦарства Твоего   <br/>\nИ когда мы в это верим нам возможно все! ', 1, 9478),
(116, 1, 'Каждый день <br/>\nвсе пути открыты<br/>\n выбирай любой!    <br/>\nДай нам Боже <br/>\nблагодати <br/>\nвыбирать любовь! ', 1, 9479),
(116, 2, 'Любовь Твоя <br/>\nживет внутри меня,  <br/>\nЧто Ты творишь <br/>\nхочу творить и я.  <br/>\nЧто видишь Ты, <br/>\nчто в сердце у Тебя. <br/>\nПусть открывает нам любовь Твоя. ', 1, 9480),
(116, 3, 'Каждый день приближает вечность ярче свет вдали .<br/>\nНе дай нам Боже <br/>\nвпасть в беспечность                             в объятиях земли. ', 1, 9481),
(116, 4, 'Дай всем сердцем устремиться<br/>\n к вышнему огню <br/>\nИ лететь  душе как птице <br/>\nк Царству Твоему ', 1, 9482),
(116, 5, 'Любовь Твоя <br/>\nживет внутри меня,  <br/>\nЧто Ты творишь <br/>\nхочу творить и я.  <br/>\nЧто видишь Ты, <br/>\nчто в сердце у Тебя. <br/>\nПусть открывает нам любовь Твоя. ', 1, 9483),
(117, 0, 'Что сказать мне?<br/>\nЖизнь отдал Ты за меня<br/>\nИ я не знаю как мне <br/>\nвыразить в словах<br/>\nТо желанье <br/>\nЧтобы посвятить Тебе<br/>\nМою душу <br/>\nи быть светом в темноте ', 1, 9484),
(117, 1, 'Все стремленье – <br/>\nдержаться Слова Твоего<br/>\nИ молюсь я чтоб познать <br/>\nмне Твой закон<br/>\nБыть в каждом шаге <br/>\nВодимым истиной Твоей<br/>\nЧтобы свет Твой <br/>\nчерез меня светил везде ', 1, 9485),
(117, 2, 'Каждый день стремлюсь <br/>\nк Тебе мой Бог<br/>\nКаждый день я следую <br/>\nза Тобой<br/>\nКаждый день жить <br/>\nдля Тебя хочу ', 1, 9486),
(117, 3, 'Ты все Тот же<br/>\nкаждый день! ', 1, 9487),
(118, 0, 'Каждый новый день <br/>\nЯ к Тебе иду,<br/>\nКаждый новый день <br/>\nЯ Тебя ищу ', 1, 9488),
(118, 1, 'Каждый новый день <br/>\nЯ Тобой дышу,<br/>\nКаждый новый день<br/>\nЯ  Тобой живу ', 1, 9489),
(118, 2, 'Тебе Единому <br/>\nславу пою, <br/>\nТебя Единого благодарю,<br/>\nТебе Единому <br/>\nмолитву возношу,<br/>\nТебя Единого люблю ', 1, 9490),
(118, 3, 'Каждый новый день <br/>\nЯ любви прошу,<br/>\nКаждый новый день <br/>\nВ небеса смотрю ', 1, 9491),
(118, 4, 'Каждый новый день <br/>\nДушу приношу,<br/>\nКаждый новый день  Твоему огню ', 1, 9492),
(661, 0, 'Какою притчей Ты Царство нам изобразил<br/>\nВ беседе личной <br/>\nкому дано, тому открыл<br/>\nИ уподобил чему так ярко Ты его?<br/>\nЧто Царство Божье<br/>\nна что похоже так оно? ', 1, 9493),
(661, 1, 'Как сокровище в поле<br/>\nКак разумные девы<br/>\nКак жемчужины поиск <br/>\nВ море брошенный невод ', 1, 9494),
(661, 2, 'Как работников плата<br/>\nИ горчичное семя<br/>\nМалая закваска<br/>\nЭто образы Царства<br/>\nТвои образы Царства ', 1, 9495),
(661, 3, 'Дай насладиться <br/>\nПлодами царства Твоего<br/>\nВ нём раствориться<br/>\nЧтоб не искать нам своего<br/>\nТак сокровенно для внешних Царствие Твоё<br/>\nСердцам смиренным пусть открывается оно ', 1, 9496),
(661, 4, 'Как работников плата<br/>\nИ горчичное семя<br/>\nМалая закваска<br/>\nДа придёт Твое Царство ', 1, 9497),
(661, 5, 'Дай продать всё богатство,<br/>\nИ раздать всё именье<br/>\nДай к Тебе стучаться<br/>\nИ откроются двери<br/>\nНищим духом даётся <br/>\nИбо их есть<br/>\nЦарство<br/>\nДай с собою расстаться,<br/>\nЧтоб найти Твоё царство ', 1, 9498),
(661, 6, 'Дай с собою расстаться,<br/>\nЧтоб найти Твоё Царство ', 1, 9499),
(662, 0, 'Так отважна в боях,<br/>\nТерпелива в скорбях<br/>\n Выступает вся стройно,<br/>\nедина, чиста ', 1, 9500),
(662, 1, 'Так послушна она<br/>\nИ скромна, как луна<br/>\nИ сияет, как солнце, <br/>\nНевеста Христа ', 1, 9501),
(662, 2, 'Как полки со знамёнами<br/>\nВся в огне закалённая<br/>\nИ вином напоённая<br/>\nВозрождённая церковь ', 1, 9502),
(662, 3, 'Восстаёт, поднимается,<br/>\nОт себя отрекается,<br/>\nВо Христа облекается<br/>\nВозрождённая церковь ', 1, 9503),
(662, 4, 'Дисциплина в рядах<br/>\nИ порядок в сердцах<br/>\nИ не дремлют солдаты<br/>\nМолитвы, поста ', 1, 9504),
(662, 5, 'Набирая бойцов,<br/>\nФормируя отцов,<br/>\nСнова в муках рождаясь,<br/>\nЯвляет Христа ', 1, 9505),
(662, 6, 'Как полки со знамёнами<br/>\nВся в огне закалённая<br/>\nИ вином напоённая<br/>\nВозрождённая церковь ', 1, 9506),
(662, 7, 'Восстаёт, поднимается,<br/>\nОт себя отрекается,<br/>\nВо Христа облекается<br/>\nВозрождённая церковь ', 1, 9507),
(662, 8, 'Церковь Свою создай<br/>\nДухом Святым была чтоб водима<br/>\nИ по Твоим следам <br/>\nДень ото дня всегда чтоб ходила ', 1, 9508),
(662, 9, 'Соедини в одно<br/>\nИ научи в молитве сражаться <br/>\nРядом к плечу плечом<br/>\nВ Духе поставь нас, <br/>\nВышли на жатву ', 1, 9509),
(662, 10, 'Как полки со знамёнами<br/>\nВся в огне закалённая<br/>\nИ вином напоённая<br/>\nВозрождённая церковь ', 1, 9510),
(662, 11, 'Восстаёт, поднимается,<br/>\nОт себя отрекается,<br/>\nВо Христа облекается<br/>\nВозрождённая церковь ', 1, 9511),
(125, 0, 'Как часто мы блуждаем<br/>\nИ просто забываем,<br/>\nЧто миром правит суета.<br/>\nПорой в любовь играем,<br/>\nГрехов не замечаем,<br/>\nА в сердце <br/>\nболь и пустота. ', 1, 9512),
(125, 1, 'Приди к Иисусу и скажи:<br/>\nЯ не хочу так дальше жить,<br/>\nЯ больше не хочу грешить.<br/>\nПрости меня.<br/>\nПриди к Иисусу и поверь,<br/>\nЧто жизнь изменится теперь<br/>\nИ что Святая Кровь Христа<br/>\nСпасёт тебя ', 1, 9513),
(125, 2, 'Господь простит любого,<br/>\nОн исцелит больного,<br/>\nНаполнит сердце добротой.<br/>\nЕго любовь святая<br/>\nЖивёт и побеждает,<br/>\nПриносит <br/>\nРадость и покой. ', 1, 9514),
(126, 0, 'Когда приходит Дух Святой <br/>\nприходит сила и свет,<br/>\n Когда приходит Дух Святой<br/>\nсияет слава побед! ', 1, 9515),
(126, 1, 'Когда приходит Дух Святой<br/>\nприходит мир и покой,<br/>\n Когда приходит Дух Святой мне сердце велит : «ВОСПОЙ!» ', 1, 9516),
(126, 2, 'Только в Тебе моя сила,<br/>\nТолько в Тебе моя слава,<br/>\nТолько в Тебе моя радость<br/>\nи только в Тебе моя жизнь ', 1, 9517),
(126, 3, 'Только Тебе буду верить,<br/>\nТолько Тебя буду славить,<br/>\nТолько Тебе поклоняться<br/>\nи только Тебе служить ', 1, 9518),
(126, 4, 'Когда приходит Дух Святой<br/>\nменяются города,<br/>\n Когда приходит Дух Святой течет живая вода ', 1, 9519),
(126, 5, 'Когда приходит Дух Святой видна дорога домой.<br/>\n Когда приходит Дух Святой<br/>\nмне сердце велит: «ВОСПОЙ!» ', 1, 9520),
(126, 6, 'Только в Тебе моя сила,<br/>\nТолько в Тебе моя слава,<br/>\nТолько в Тебе моя радость<br/>\nи только в Тебе моя жизнь ', 1, 9521),
(126, 7, 'Только Тебе буду верить,<br/>\nТолько Тебя буду славить,<br/>\nТолько Тобой утешаться<br/>\nи только Тебя любить ', 1, 9522),
(128, 0, 'Когда я падал низко-низко<br/>\nТы был ко мне так близко-близко<br/>\nНе осуждал, не укорял меня<br/>\nА просто утешал<br/>\nОсвобождал от одиночества<br/>\nИ оживлял Свои пророчества<br/>\nИ говорил, что всё закончится<br/>\nЧтоб я Тебе доверял ', 1, 9523),
(128, 1, 'Такой чудесный славный <br/>\nтакой родной<br/>\nТакой прекрасный, самый мой дорогой<br/>\nТакой великий, сильный <br/>\nО мой Господь, я так люблю Тебя!<br/>\nК  Тебе стремлюсь <br/>\nя сердцем Тебя зову<br/>\nВсё оставляю, снова к Тебе бегу<br/>\nВедь оставаться мне без Тебя нельзя<br/>\nВ Тебе вся жизнь моя ', 1, 9524),
(128, 2, 'Мне без Тебя так сложно-сложно<br/>\nМой самый верный Друг, надёжный<br/>\nВсё больше понимаю сколько же<br/>\nТы значишь в моей судьбе<br/>\nЯ не хочу, чтоб обстоятельства<br/>\nМеня вводили в замешательство<br/>\nВедь это то же, что предательство –<br/>\nКогда сомневаюсь в Тебе ', 1, 9525),
(128, 3, 'Такой чудесный, славный <br/>\nтакой родной<br/>\nТакой прекрасный, самый мой дорогой<br/>\nТакой великий, сильный <br/>\nО мой Господь, я так люблю Тебя!<br/>\nК  Тебе стремлюсь <br/>\nя сердцем Тебя зову<br/>\nВсё оставляю, снова к Тебе бегу<br/>\nВедь оставаться мне без Тебя нельзя<br/>\nВ Тебе вся жизнь моя ', 1, 9526),
