(224, 0, 'Сколько раз <br/>\nбеспечно я<br/>\nШёл куда-то без Тебя<br/>\nНе спросив <br/>\nсовета Твоего<br/>\nМой Бог ', 1, 10146),
(224, 1, 'И моё старание<br/>\nВозводило здание<br/>\nНо оно не строилось<br/>\nВсё потому <br/>\nЧто не ждал Тебя ', 1, 10147),
(224, 2, 'Боже, <br/>\nдай мне знать <br/>\nКогда идти,<br/>\nЧтобы не отстать <br/>\nОт Тебя в пути ', 1, 10148),
(224, 3, 'А когда Тебя <br/>\nНадо подождать <br/>\nА когда <br/>\nза Тобой бежать ', 1, 10149),
